func:
var preamble = msg.payload.substr(0,2);
if (preamble == ":T")
{
    msg.topic = 'Temperature'
    var temperature = msg.payload.substr(2,6);
    if (isNaN(temperature))
    {
        temperature = 0; /* insert zero if bad number */
    }
    msg.payload = temperature;
    return msg;
}
return null;

C = msg.payload;
F = (C * 1.8) + 32;
msg.payload = F;
return msg;

if (msg.payload == undefined)
{
    return null;
}
return msg;

var preamble = msg.payload.substr(0,2);
msg.topic = 'Mode';
if (preamble == ":C")
{
    msg.payload = "Auto";
    return msg;
}
else if (preamble == ":M")
{
    msg.payload = "Manual";
    return msg;
}
return null;

var preamble = msg.payload.substr(0,2);
if (preamble == ":H")
{
    msg.topic = 'Heater Setting'
    var setting = msg.payload.substr(2,3);
    msg.payload = setting;
    global.set('heater', setting)
    return msg;
}
return null;

var preamble = msg.payload.substr(0,2);
if (preamble == ":F")
{
    msg.topic = 'Fan Setting'
    var setting = msg.payload.substr(2,3);
    msg.payload = setting;
    global.set('fan',setting)
    return msg;
}
return null;

F = msg.payload;
msg.payload = Math.round(F*10)/10;
return msg;

var preamble = msg.payload.substr(0,2);
msg.topic = 'Drum';
if (preamble == ":+")
{
    msg.payload = ":>+/"; /* On */
    return msg;
}
else if (preamble == ":-")
{
    msg.payload = ":>-/"; /* Off */
    return msg;
}
return null;

percent = Math.round(msg.payload * 100);
msg.payload = percent;
msg.topic = "Heater"
return msg;

/* 
Notice the minus sign before the rounding. The PID node
reports the value in the opposite sign of how it is applied
to the output. I prefer changing the sign for display purposes.
*/
prop = -Math.round(msg.proportional * 100)/100;
msg.payload = prop;
return msg;

/* 
Notice the minus sign before the rounding. The PID node
reports the value in the opposite sign of how it is applied
to the output. I prefer changing the sign for display purposes.
*/
integ = -Math.round(msg.integral * 100)/100;
msg.payload = integ;
return msg;

/* 
Notice the minus sign before the rounding. The PID node
reports the value in the opposite sign of how it is applied
to the output. I prefer changing the sign for display purposes.
*/
der = -Math.round(msg.derivative * 100)/100;
msg.payload = der;
return msg;

var sp = msg.setpoint;
msg.payload = sp;
msg.topic = "Target";
return msg;

/* 
 Scale the output to limit max outuput to 80%. 
 This is done to extend the life of the heater elements.
*/
output = msg.payload;
msg.payload = Math.round(output * 0.8);
return msg;

node_status = msg.status.text;
node_color = msg.status.fill;
if(node_color == undefined)
{
    return [null, null];
}

if(node_status == undefined)
{   /* status is blank when temp is in the proportional band */
    node_status = "In The Zone";
}

if(node_color == "green")
{ /* node enabled and all ok */
    output = 1;
}
else
{   /* node disabled or error status */
    output = 0;
}

msg.payload = output;
msg.topic = "PID_status";
var msg_text = {payload:node_status, topic:"PID_status_text"};
return [msg, msg_text];

/* 
 * Intended to detect shorting or disconnection of the 
 * thermocouple. This is to prevent the PID loop from 
 * going into full heat due to a misreading thermocouple.
 */
 var temperature = msg.payload;
 if (temperature < 40)
 {
     var count = context.get("OutOfRangeCount");
     if (count++ > 10) 
     { /* if temp has been out of range 10 times in a row */
        /* send a disable message to the PID loop */
        msg.topic = "enable";
        msg.payload = 0;
        return msg;
     }
     context.set("OutOfRangeCount", count);
 }
 else
 { /* temperature ok */
    context.set("OutOfRangeCount", 0);
 }
return null;

initialize:
// Code added here will be run once
// whenever the node is started.
context.set('OutOfRangeCount', 0);

func:
var sendMessage = false;
var average = flow.get('temperature_average');
if (!average) average = 0;
var count = flow.get('temperature_count');
if (!count) count = 0;

switch(msg.topic)
{
    case "Temperature":
        average += msg.payload;
        count++;
        if (count >= 10) {
            msg.payload = average /count;
            count = 0;
            average = 0;
            sendMessage = true;
        }
        flow.set('temperature_average', average);
        flow.set('temperature_count', count);
        break;
    case "enable":
        /* send a temperature to trigger the enable */
        if(count > 0)
        {
            /* send the partially accumulated average */
            msg.payload = average / count;
        }
        else
        {
            /* Unfortunately we have just reset the 
            * average so don't have a temperature sample
            * yet. In this case send a high temperature.
            * This is safer than sending a low temperature
            * since it should cause the heater output 
            * to reduce rather than increase.
            */
            msg.payload = 400; 
        }
        msg.topic = "Temperature";
        sendMessage = true;
        break;
}

if(sendMessage == true)
{
    return msg;     
}
else
{
    return null;
}

percent = msg.payload;

if (percent < 10)
{
    percent_string = "00"+ percent;
}
else if (percent < 100)
{
    percent_string = "0" + percent;
}
else
{
    percent_string = percent;
}

if (msg.topic == "Heater")
{
    msg.payload = ":>H" + percent_string + "/";
}
else if (msg.topic == "Fan")
{
    msg.payload = ":>F" + percent_string +"/";
}

return msg;

var text = msg.payload;
msg.payload = text.substr(-20, 18);
return msg;

h = context.get('hours');
m = context.get('minutes');
s = context.get('seconds');
on = context.get('running');
if(on)
{
    if(msg.topic == "tick")
    {
        s += 1;
        if(s > 59)
        {
            s = 0;
            m += 1;
        }
        if(m > 59)
        {
            m = 0;
            h += 1;
        }
    }
}
if(msg.topic == "start")
{
    context.set('running', true);
}
if(msg.topic == "stop")
{
    context.set('running', false);
    h = 0;
    m = 0;
    s = 0;
}
context.set('hours', h);
context.set('minutes', m);
context.set('seconds', s);

/* format the timer string */
h = h.toString();
m = m.toString();
s = s.toString();
/* add leading zeros if needed */
if(h.length < 2) h = "0" + h;
if(m.length < 2) m = "0" + m;
if(s.length < 2) s = "0" + s;
/* pass the final string in the message payload */
msg.payload = h + ":" + m + ":" + s;
return msg;

