func:
preamble = msg.payload.substr(0,2);
if (preamble == ":T")
{
    msg.topic = 'Temperature'
    temperature = msg.payload.substr(2,6);
    if (isNaN(temperature))
    {
        temperature = 0; /* insert zero if bad number */
    }
    msg.payload = temperature;
    return msg;
}
return null;

C = msg.payload;
F = (C * 1.8) + 32;
msg.payload = F;
return msg;

if (msg.payload == undefined)
{
    return null;
}
return msg;

preamble = msg.payload.substr(0,2);
msg.topic = 'Mode';
if (preamble == ":C")
{
    msg.payload = "Auto";
    return msg;
}
else if (preamble == ":M")
{
    msg.payload = "Manual";
    return msg;
}
return null;

preamble = msg.payload.substr(0,2);
if (preamble == ":H")
{
    msg.topic = 'Heater Setting'
    setting = msg.payload.substr(2,3);
    msg.payload = setting;
    global.set('heater', setting)
    return msg;
}
return null;

preamble = msg.payload.substr(0,2);
if (preamble == ":F")
{
    msg.topic = 'Fan Setting'
    setting = msg.payload.substr(2,3);
    msg.payload = setting;
    global.set('fan',setting)
    return msg;
}
return null;

percent = Math.round(msg.payload * 100);
msg.payload = percent;
msg.topic = "Heater"
return msg;

/* 
Notice the minus sign before the rounding. The PID node
reports the value in the opposite sign of how it is applied
to the output. I prefer changing the sign for display purposes.
*/
prop = -Math.round(msg.proportional * 100)/100;
msg.payload = prop;
return msg;

/* 
Notice the minus sign before the rounding. The PID node
reports the value in the opposite sign of how it is applied
to the output. I prefer changing the sign for display purposes.
*/
integ = -Math.round(msg.integral * 100)/100;
msg.payload = integ;
return msg;

/* 
Notice the minus sign before the rounding. The PID node
reports the value in the opposite sign of how it is applied
to the output. I prefer changing the sign for display purposes.
*/
der = -Math.round(msg.derivative * 100)/100;
msg.payload = der;
return msg;

sp = msg.setpoint;
msg.payload = sp;
msg.topic = "Setpoint";
return msg;

/* 
 Scale the output to limit max outuput to 80%. 
 This is done to extend the life of the heater elements.
*/
output = msg.payload;
msg.payload = Math.round(output * 0.8);
return msg;

percent = msg.payload;

if (percent < 10)
{
    percent_string = "00"+ percent;
}
else if (percent < 100)
{
    percent_string = "0" + percent;
}
else
{
    percent_string = percent;
}

if (msg.topic == "Heater")
{
    msg.payload = ":>H" + percent_string + "/";
}
else if (msg.topic == "Fan")
{
    msg.payload = ":>F" + percent_string +"/";
}

return msg;

F = msg.payload;
msg.payload = Math.round(F*10)/10;
return msg;

c = context.get('count');
c += 1;
context.set('count', c)
if (c > 9)
{
    context.set('count', 0)
    return msg;
}
return null;

