func:
if (msg.payload == "change")
{
    global.set('ui_tab', msg.tab);
    node.status({fill:"green", shape:"dot", text:"Tab "+msg.tab});
}
return msg;

node_status = msg.status.text;
node_color = msg.status.fill;
if(node_color == undefined)
{
    return [null, null];
}

if(node_status == undefined)
{   /* status is blank when temp is in the proportional band */
    node_status = "In The Zone";
}

if(node_color == "green")
{ /* node enabled and all ok */
    output = 1;
}
else
{   /* node disabled or error status */
    output = 0;
}

msg.payload = output;
msg.topic = "PID_status";
var msg_text = {payload:node_status, topic:"PID_status_text"};
return [msg, msg_text];

/* 
 * Intended to detect shorting or disconnection of the 
 * thermocouple. This is to prevent the PID loop from 
 * going into full heat due to a misreading thermocouple.
 */
 temperature = msg.payload;
 if (temperature < 40)
 {
     count = context.get("OutOfRangeCount");
     if (count++ > 10) 
     { /* if temp has been out of range 10 times in a row */
        /* send a disable message to the PID loop */
        msg.topic = "enable";
        msg.payload = 0;
        return msg;
     }
     context.set("OutOfRangeCount", count);
 }
 else
 { /* temperature ok */
    context.set("OutOfRangeCount", 0);
 }
return null;

