func:
h = context.get('hours');
m = context.get('minutes');
s = context.get('seconds');
on = context.get('running');
if(on)
{
    if(msg.topic == "tick")
    {
        s += 1;
        if(s > 59)
        {
            s = 0;
            m += 1;
        }
        if(m > 59)
        {
            m = 0;
            h += 1;
        }
    }
}
if(msg.topic == "start")
{
    context.set('running', true);
}
if(msg.topic == "stop")
{
    context.set('running', false);
    h = 0;
    m = 0;
    s = 0;
}
context.set('hours', h);
context.set('minutes', m);
context.set('seconds', s);

/* format the timer string */
h = h.toString();
m = m.toString();
s = s.toString();
/* add leading zeros if needed */
if(h.length < 2) h = "0" + h;
if(m.length < 2) m = "0" + m;
if(s.length < 2) s = "0" + s;
/* pass the final string in the message payload */
msg.payload = h + ":" + m + ":" + s;
return msg;

